mapper.select:
select `id`, `count_date`, `today_reg_user`, `today_wx_reg_user`, `today_order_user`, `count_user_num`, `today_pay_order`, `today_unpay_order`, `today_user_order`, `count_order_num`, `today_trade_money`, `count_trade_money`, `add_time` from nideshop_statistics WHERE 1=1
and count_date>=#{count_date_begin}
and count_date<=#{count_date_end}
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}
